import React from "react";

import "../styles/SignIn.css";

class SignIn extends React.Component {
	constructor(props) {
		super(props);

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);

		this.state = {
			email: '',
			password: '',
			isEmailValid: true,
			isPasswordValid: true
		}
	}

	handleChange(event) {
		const value = event.target.value;
		const name  = event.target.name;

		this.setState({
			[name]: value
		});
	}

	handleSubmit(event) {
		console.log(this.state);

		event.preventDefault();

		if (this.state.email === 'xablau@email.com') {
			this.setState({
				isEmailValid: false
			});
		}
	}

	render() {
		const invalidEmailClassName = !this.state.isEmailValid ? 'Invalid' : '';
		const invalidPasswordClassName = !this.state.isPasswordValid ? 'Invalid' : '';

		return(
			<div className="SignIn">
				<form className="Form" onSubmit={this.handleSubmit}>
					<label>Email: </label>
					<Input
						type="email"
						name="email"
						value={this.state.email}
						onChange={this.handleChange}
						className={invalidEmailClassName}/>

					<label>Password: </label>
					<Input
						type="password"
						name="password"
						value={this.state.password}
						onChange={this.handleChange}
						className={invalidPasswordClassName}/>

					<input type="submit" className="Submit" value="Log In"/>
				</form>
			</div>
		);
	}
}

function Input(props) {
	return(
		<input
			type={props.type}
			name={props.name}
			value={props.value}
			onChange={props.onChange}
			className={props.className}/>
	);
}

export default SignIn;
