import React from "react";

import "../styles/SignIn.css";

class SignIn extends React.Component {
	constructor(props) {
		super(props);

		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleSubmit() {

	}

	render() {
		return(
			<div className="SignIn">
				<form className="Form" onSubmit={this.handleSubmit}>
					<Input labelValue="Email:" inputType="email" inputName="sign_in[email]" />
					<Input labelValue="Password:" inputType="password" inputName="sign_in[password]" />

					<input type="submit" className="Submit" value="Log In"/>
				</form>
			</div>
		);
	}
}

class Input extends React.Component {
	constructor(props) {
		super(props);

		this.handleChange = this.handleChange.bind(this);

		this.state = {
			value: ''
		};
	}

	handleChange(event) {
		const value = event.target.value;

		this.setState({
			value: value
		});
	}

	render() {
		return(
			<>
				<label>{this.props.labelValue}</label>
				<input type={this.props.inputType} name={this.props.inputName} value={this.state.value} onChange={this.handleChange}/>
			</>
		);
	}
}

export default SignIn;
